// Code for card function
const cartReducer = (state=[], action) => {
  switch(action.type) {

    case "ADD":
      // ... is spread operator which is used to take ecisting item and dump it into new array
      return[...state, action.payload]

    case "REMOVE":
      // this will remove first index item from cart, not all of an item from cart
      const firstMatchIndex = state.indexOf(action.payload)
      return state.filter((item, index) => index !== firstMatchIndex)

      default:
        return state;
    }
}

export default cartReducer
